var givenNameMissing = "Given Name for the first Inventor listed is required.";
var familyNameMissing = "Family Name for the first Inventor listed is required.";

function validateSfApplicant()
{
	var errorMsg = "";

	var givenName = ContentArea1.sfApplicantInformation.sfApplicantName.firstName.rawValue;

	if ((givenName == null) || (givenName == ""))
	{
		errorMsg = errorMsg + '\n' + givenNameMissing;
	}

	var lastName = ContentArea1.sfApplicantInformation.sfApplicantName.lastName.rawValue;

	if ((lastName == null) || (lastName == ""))
	{
		errorMsg = errorMsg + '\n' + familyNameMissing;
	}

	return errorMsg;
}

var givenNameMissing = "Given Name for the first Inventor listed is required.";
var familyNameMissing = "Family Name for the first Inventor listed is required.";
var customerNumnsg = "Customer Number for correspondence information is required.";
var name1Msg = "Name 1 for correpondence information is required.";
var add1Msg = "Address 1 for correspondence information is required.";
var cityMsg = "City for correspondence information is required.";
var countryMsg = "Country for correspondence information is required.";


function validateSfApplicant()
{
	var errorMsg = "";

	var givenName = ContentArea1.sfApplicantInformation.sfApplicantName.firstName.rawValue;

	if ((givenName == null) || (givenName == ""))
	{
		errorMsg = errorMsg + '\n' + givenNameMissing;
	}

	var lastName = ContentArea1.sfApplicantInformation.sfApplicantName.lastName.rawValue;

	if ((lastName == null) || (lastName == ""))
	{
		errorMsg = errorMsg + '\n' + familyNameMissing;
	}

	var chk = ContentArea2.sfCorrepondInfo.corresInfoChk.rawValue;
	if(chk == 0)
	{
		var cNum = ContentArea2.sfCorrCustNo.customerNumber.rawValue;
		if(cNum == null || cNum == "")
		{
			errorMsg = errorMsg + '\n' + customerNumnsg;
		}
	}
	else
	{
		var name1 = ContentArea2.sfCorrAddress.Name1.rawValue;
		if(name1 == null || name1 == "")
		{
			errorMsg = errorMsg + '\n' + name1Msg;
		}
		var add1 = ContentArea2.sfCorrAddress.address1.rawValue;
		if(add1 == null ||  add1 == "")
		{
			errorMsg = errorMsg + '\n' + add1Msg;
		}
		var cityN = ContentArea2.sfCorrAddress.city.rawValue;
		if(cityN == null ||  cityN == "")
		{
			errorMsg = errorMsg + '\n' + cityMsg;
		}
		var ctryMsg = ContentArea2.sfCorrAddress.corrCountry.rawValue;
		if(ctryMsg == null ||  ctryMsg == "")
		{
			errorMsg = errorMsg + '\n' + countryMsg;
		}


	}
	return errorMsg;
}

