variables.script:
var customerNumberMessage = "Customer Number must be a minimum of 9 characters;";

function validateCorrespondence()
{
	var errorMsgValCorrespondence = "";

	var hasCustomerNumber = ContentArea2.sfCorrepondInfo.corresInfoChk.rawValue;
	var customerNumber = ContentArea2.sfCorrCustNo.customeNumber.rawValue;
	if ((hasCustomerNumber == 1) && ((customerNumber == null) || (customerNumber.length < 9)))
	{
		errorMsgValCorrespondence = errorMsgValCorrespondence + '\n' + customerNumberMessage;
	}
}

